dt-event.example:
init: function(that, fn, preDefined = null) {
    let el = $('<select>');
    ...
    $(el).on('dtsb-inserted', function(){
        for(let element of el) {
            if($(el).hasClass('select2-hidden-accessible')) {
                $(el).select2('destroy');
            }

            $(el).prepend('<option selected></option>');
            $(el).select2({placeholder:'Value'});

            if(preDefined !== null) {
                $(el).val(preDefined[0]);
                $(el).trigger('input');
            }
        }
    }
    ...
})
