dt-option.example:
new DataTable('#myTable', {
	layout: {
		top1: {
			searchBuilder: {
				preDefined: {
					criteria: [
						{
							condition: '=',
							data: 'Office',
							value: ['Edinburgh']
						}
					],
					logic: 'AND'
				}
			}
		}
	}
});

new DataTable('#myTable', {
	layout: {
		top1: {
			searchBuilder: {
				preDefined: {
					criteria: [
						{
							condition: '=',
							data: 'Office',
							value: ['Edinburgh']
						},
						{
							criteria: [
								{
									condition: '=',
									data: 'Name',
									value: ['Cedric Kelly']
								},
								{
									condition: '=',
									data: 'Name',
									value: ['Dai Rios']
								}
							],
							logic: 'OR'
						}
					],
					logic: 'AND'
				}
			}
		}
	}
});

new DataTable('#myTable', {
	layout: {
		top1: {
			searchBuilder: {
				preDefined: {
					criteria: [
						{
							condition: '=',
							data: 'Office',
							origData: 'office',
							type: 'string',
							value: ['Edinburgh']
						}
					],
					logic: 'AND'
				}
			}
		}
	}
});
