dt-option.example:
$(document).ready(function() {
	$('#example').DataTable( {
		language: {
			searchBuilder: {
				conditions :{
                    array: {
                        contains: 'Contains',
						empty: 'Empty',
						equals: 'Equals',
						not: 'Not',
						notEmpty: 'Not Empty',
						without: 'Without'
                    }
                }
			}
		},
		dom: 'Qlfrtip',
	});
});
