dt-option.example:
$(document).ready(function() {
	$('#example').DataTable( {
		language: {
			searchBuilder: {
				conditions :{
                    date: {
                        after: 'After',
                        before: 'Before',
                        between: 'Between',
						empty: 'Empty',
                        equals: 'Equals',
                        not: 'Not',
                        notBetween: 'Not Between',
                        notEmpty: 'Not Empty'
                    }
                }
			}
		},
		dom: 'Qlfrtip',
	});
});
