dt-option.example:
new DataTable('#myTable', {
	language: {
		searchBuilder: {
			conditions: {
				string: {
					contains: 'Contains',
					null: 'Empty',
					ends: 'Ends With',
					=: 'Equals',
					!=: 'Not',
					!contains: 'Does Not Contain',
					!null: 'Not Empty',
					!ends: 'Does Not End With',
					!starts: 'Does Not Start With',
					starts: 'Starts With'
				}
			}
		}
	},
	layout: {
		top1: 'searchBuilder'
	}
});
