dt-option.example:
$(document).ready(function() {
	$('#example').DataTable( {
		language: {
			searchBuilder: {
				conditions :{
                    string: {
                        contains: 'Contains',
                        empty: 'Empty',
                        endsWith: 'Ends With',
                        equals: 'Equals',
                        not: 'Not',
						notContains: 'Does Not Contain',
                        notEmpty: 'Not Empty',
						notEndsWith: 'Does Not End With',
						notStartsWith: 'Does Not Start With',
                        startsWith: 'Starts With',
                    }
                }
			}
		},
		dom: 'Qlfrtip',
	});
});
