dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchBuilder: {
                title: 'Search Builder Title'
            }
        },
        buttons: [ 'searchBuilder' ],
        dom: 'Bfrtip'
    });
});

$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchBuilder: {
                title: {
					0: 'Search Builder',
					_: 'Search Builder (%d)'
				}
            }
        },
        titles:[
            'SearchBuilder'
        ],
        dom: 'Bfrtip',
    });
});
