dt-option.example:
$('#example').DataTable( {
    dom: 'Qfrtip',
    searchBuilder: {
        conditions: {
            "date":[{
                isInputValid(val, that) {
                    let allFilled = true;
                    for (let v of val) {
                        if ($(v).is('input') && $(v).val().length === 0) {
                            allFilled = false;
                        }
                    }

                    return allFilled;
                }
                conditionName: 'Made up',
                inputValue(val, value = null) {
                    let values = [];

                    if (value === null) {
                        for (let v of val) {
                            if ($(v).is('input')) {
                                values.push($(v).val());
                            }
                        }
                    }
                    else {
                        for (let v = 0; v < val.length; v++) {
                            if ($(val[v]).is('input')) {
                                $(val[v]).val(value[v]);
                                values.push(value[v]);
                            }
                        }
                    }

                    return values;
                },
                init(that, fn, preDefined) {
                    let el = $('<input/>').addClass(Criteria.classes.value).addClass(Criteria.classes.input).dtDateTime();

                    el.on('input', function() {fn(that, el)});

                    if (preDefined !== undefined) {
                        $(el).val(preDefined[0]);
                    }

                    return el;
                },
                search (value, comparison) {
                    return value === comparison[0];
                }
            }]
        }
    }
});
