dt-option.example:
$('#example').DataTable( {
    dom: 'Qfrtip',
    searchBuilder: {
        conditions: {
            "html":[{
                display: 'Made up Equals',
                comparator: function(value, comparison) {
                    return value === comparison[0];
                },
                type: 'select',
                valueInputs: 1
            }]
        }
    }
});
