dt-option.example:
$('#example').DataTable( {
    dom: 'Qfrtip',
    searchBuilder: {
        conditions: {
            "num-fmt":[{
                display: 'Equals',
                comparator(value: any, comparison: any[]): boolean {
                    value = value.replace(/[^0-9.]/g, '');
                    comparison[0] = comparison[0].replace(/[^0-9.]/g, '');

                    return +value === +comparison[0];
                },
                type: 'select',
                valueInputs: 1
            }]
        }
    }
});
