dt-option.example:
...
active (val, that) {
    $(that.dom.valueTitle).attr('selected', false);
    let allFilled = true;

    for (let v of val) {
        if ($(v).has('option:selected').length < 1) {
            allFilled = false;
        }
    }

    return allFilled;
}
