$('#example').DataTable( {
    dom: 'Qfrtip',
    searchBuilder: {
        conditions[type]: {
            "num":[{
                display: 'Made up',
                comparator: function(value, comparison) {
                    return +value === +comparison[0];
                },
                type: 'select',
                valueInputs: 1
            }]
        }
    }
});
