dt-option.example:
    ...
    inputValue (val, value = null) {
        let values = [];

		if (value === null) {
			for (let v of val) {
				if ($(v).is('select')) {
					values.push($(v).children('option:selected').val());
				}
			}
		}
		else {
			for (let v = 0; v < val.length; v ++) {
				if ($(val[v]).is('select')) {
					let children = $(v).children().toArray;
					for (let child of children) {
						if ($(child).val() === value[v]) {
							$(child).attr('selected', true);
							values.push(value[v]);
						}
					}
				}
			}
		}

		return values;
    }
