    ...
    inputValue (val, value = null) {
		let values = [];

		if (value === null) {
			for (let v of val) {
				if ($(v).is('input')) {
					values.push($(v).val());
				}
			}
		}
		else {
			for (let v = 0; v < val.length; v++) {
				if ($(val[v]).is('input')) {
					$(val[v]).val(value[v]);
					values.push(value[v]);
				}
			}
		}

		return values;
    }
