dt-option.example:
$('#example').DataTable( {
    dom: 'Qfrtip',
    searchBuilder: {
        conditions[type]: {
            "num":[{
                display: 'Outwith Exclusive',
                comparator(value: any, comparison: any[]): boolean {
                    if (comparison[0] < comparison[1]) {
                        return !(+comparison[0] <= +value && +value <= +comparison[1]);
                    }
                    else {
                        return !(+comparison[1] <= +value && +value <= +comparison[0]);
                    }
                },
                joiner: 'and',
                type: 'input',
                valueInputs: 2,
            }]
        }
    }
});
