dt-example.js:
$(document).ready(function() {
    var stored;

    $('#example').DataTable( {
        dom: 'Qfrtip'
    });

    $('#getDetails').on('click', function() {
        stored = $('#example').DataTable().searchBuilder.getDetails();
        console.log(stored);
    })

    $('#revert').on('click', function() {
        stored = $('#example').DataTable().searchBuilder.rebuild(stored);
    })
});
