dt-example.js:
$('#example').DataTable({
	language: {
		searchBuilder: {
			button: 'Filter'
		}
	},
	layout: {
		top1: {
			buttons: ['searchBuilder']
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	language: {
		searchBuilder: {
			button: 'Filter'
		}
	},
	layout: {
		top1: {
			buttons: ['searchBuilder']
		}
	}
});
