dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        dom: 'Qfrtip',
        searchBuilder: {
            conditions: {
                "num":[{
                    display: 'Made up',
                    comparator: function(value, comparison) {
                        return +value === +comparison[0];
                    },
                    type: 'select',
                    valueInputs: 1
                }]
            }
        }
    });
});
