dt-example.js:
    $('#example').DataTable({
	language: {
		searchBuilder: {
			add: '+',
			condition: 'Comparator',
			clearAll: 'Reset',
			delete: 'Delete',
			deleteTitle: 'Delete Title',
			data: 'Column',
			left: 'Left',
			leftTitle: 'Left Title',
			logicAnd: '&&',
			logicOr: '||',
			right: 'Right',
			rightTitle: 'Right Title',
			title: {
				0: 'Filters',
				_: 'Filters (%d)'
			},
			value: 'Option',
			valueJoiner: 'et'
		}
	},
	layout: {
		top1: 'searchBuilder'
	}
});

