dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchBuilder: {
                add: '+',
                condition: 'Comparator',
                clearAll: 'Reset',
                deleteTitle: 'Delete',
                data: 'Column',
                leftTitle: 'Left',
                logicAnd: 'And',
                logicOr: 'Or',
                rightTitle: 'Right',
                title: {
                    0: 'Filters',
                    _: 'Filters (%d)'
                },
                value: 'Option',
            }
        },
        dom: 'Qlfrtip',
    });
});
