dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchBuilder: {
                add: '+',
                condition: 'Comparator',
                clearAll: 'Reset',
                delete: 'Delete',
                deleteTitle: 'Delete',
                data: 'Column',
                left: 'Left',
                leftTitle: 'Left',
                logicAnd: '&&',
                logicOr: '||',
                right: 'Right',
                rightTitle: 'Right',
                title: {
                    0: 'Filters',
                    _: 'Filters (%d)'
                },
                value: 'Option',
                valueJoiner: 'et'
            }
        },
        dom: 'Qlfrtip',
    });
});
