dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchBuilder: {
                add: '+',
                condition: 'Comparator',
                clearAll: 'Reset',
                deleteTitle: 'Delete',
                field: 'Column',
                leftTitle: 'Left',
                logicAnd: 'And',
                logicOr: 'Or',
                rightTitle: 'Right',
                title: 'Search Builder Title',
                value: 'Option',
            }
        },
        dom: 'Qfrtip',
    });
});
