dt-example.js:
$(document).ready(function() {
    $.fn.dataTable.ext.searchBuilder.conditions.special = [{
        isInputValid: function (el, that) {
            if($(el[0]).attr('active') === "false"){
                $(el[0]).attr('active', true);
                return true;
            }
            else {
                $(el[0]).attr('active', false);
                return false;
            }
        },
        conditionName: 'Edinburgh',
        inputValue: function (el, that) {
            return ['Edinburgh']
        },
        init: function (that, fn, preDefined) {
            var el = $('<button>Edinburgh</button>').attr('active', false);

            return el.on('click', function() {fn(that, el)});
        },
        search: function ( value, comparison) {
            return value === comparison[0];
        }
    }]

    var dt = $('#example').DataTable( {
        dom: 'Qfrtip',
        columnDefs: [{type: "special", targets: 2}]
    });

});
