dt-example.js:
var dt = $('#example').DataTable({
	layout: {
		top1: 'searchBuilder'
	}
});

dt.cell(2, 2).data('');
dt.cell(4, 2).data('');
dt.cell(6, 2).data('');
dt.cell(2, 3).data('');

dt-example.js-vanilla:
let dt = new DataTable('#example', {
	layout: {
		top1: 'searchBuilder'
	}
});

dt.cell(2, 2).data('');
dt.cell(4, 2).data('');
dt.cell(6, 2).data('');
dt.cell(2, 3).data('');
