dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Qlfrtip',
        searchBuilder: {
            preDefined: {
                criteria:[
                    {
                        condition: 'Equals',
                        dataTitle: 'Office',
                        value: ['Edinburgh']
                    },
                    {
                        criteria: [
                            {
                                condition: 'Equals',
                                dataTitle: 'Name',
                                value: ['Cedric Kelly']
                            },
                            {
                                condition: 'Equals',
                                dataTitle: 'Name',
                                value: ['Dai Rios']
                            }
                        ],
                        logic: 'OR'
                    }
                ],
                logic: 'AND'
            }
        }
    });
});
