dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Qlfrtip',
        searchBuilder: {
            preDefined: {
                criteria:[
                    {
                        condition: 'Equals',
                        data: '2',
                        type: 'criteria',
                        value: ['Edinburgh']
                    },
                    {
                        criteria: [
                            {
                                condition: 'Equals',
                                data: '0',
                                type: 'criteria',
                                value: ['Cedric Kelly']
                            },
                            {
                                condition: 'Equals',
                                data: '0',
                                type: 'criteria',
                                value: ['Dai Rios']
                            }
                        ],
                        logic: 'OR',
                        type: 'group'
                    }
                ],
                logic: 'AND',
                type: 'group'
            }
        }
    });
});
