	ajax: '../../../../examples/ajax/data/data_5k.txt',
	columns: [
		{ data: 'name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'extn' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	layout: {
		top1: 'searchBuilder'
	}
});

